bool swapBuffers()      // Setzt Pointer zurück, sobald sie das Ende erreicht haben
{
  if (writeptr == &ekgBuffer[BUFFER_SIZE])
  {
    Serial.printf("\nWriteptr before Reset %d", (writeptr - ekgBuffer)/sizeof(uint16_t));
    Serial.printf("\nDisplayptr: %d ", (displayptr - ekgBuffer)/sizeof(uint16_t));
    writeptr = begin;
    Serial.println("\nReset writeptr\n");
  }
  if (displayptr == &ekgBuffer[BUFFER_SIZE])    // Kann writeptr nicht überholen, weil er nur beim Interrupt aufgerufen wird
  {
    Serial.printf("\nDisplayptr before Reset %d", (displayptr-ekgBuffer)/sizeof(uint16_t));
    Serial.printf("\nWriteptr: %d", (writeptr-ekgBuffer)/sizeof(uint16_t));
    displayptr = begin;
    Serial.println("\nReset displayptr\n");
  }
  if (sendptr == &ekgBuffer[BUFFER_SIZE])   // Check wird in der Sendefunktion gemacht
  {
    
    Serial.printf("\nSendptr before Reset %d ", (sendptr-ekgBuffer)/sizeof(uint16_t));
    Serial.printf("\nWriteptr: %d", (writeptr-ekgBuffer)/sizeof(uint16_t));
    sendptr = begin;
    Serial.println("\nReset sendptr\n");
  }
  else
    return false;
  return true;
}


//double IIR_Notch_Filter(uint16_t * sendptr)                                             // Filter-Funktion auf Eingangsignal: Infiinite Impulse Response (unendliche Impulsantwort)
bool IIR_Notch_Filter(uint16_t * &sendptr)
{
  // 

  for (int i = 0; i < BUFFER_SIZE/2; i++)   // Alle Werte des halben Buffers filtern 
  {
    Mem0 = *sendptr - coeffs.a[1] * Mem1 - coeffs.a[2] * Mem2;          // Berechnung von Mem0 
    *sendptr = static_cast<uint16_t>(coeffs.b[0] * Mem0 + coeffs.b[1] * Mem1 + coeffs.b[2] * Mem2);    // Ermittlung des gefilterten Werts
    Mem2 = Mem1;                                                         // Abspeichern der berechneten Werte
    Mem1 = Mem0; 

    sendptr++;
  }

  
  return true;
}


bool sendEKGdata(bool forceSend)
{
  // UDP.beginPacket(UDP.remoteIP(), UDP.remotePort()); // War ursprünglich hier
  if ((writeptr == middle || writeptr == begin) || forceSend)   // Passt == begin? Oder == BUFFERSIZE?
  {
    UDP.beginPacket(UDP.remoteIP(), UDP.remotePort());
    Serial.printf("\nBuffer wird gesendet\nSendptr Stelle %d" , (sendptr-ekgBuffer)/sizeof(uint16_t));
    Serial.printf("\nWriteptr Stelle %d\n", (writeptr-ekgBuffer)/sizeof(uint16_t));

    UDP.write((uint8_t*)sendptr, BUFFER_SIZE);  // Hälfte des Buffers schicken (8 bit) -> 3750 Werte
    sendptr = sendptr + BUFFER_SIZE/2;
    UDP.endPacket();
    return true;
  }
  
  //UDP.endPacket(); // war ursprünglich so
  //Serial.println("Sent");
  return false;
}

void loop()
{
  txval = (int) 127*(1+sin(2*PI*millis()/1000)); // sin ist im Bereich [0;2]. Mit *127 wird gesamter 8bit Bereich genutzt.
  dacWrite(DAC_PIN, txval);

  if (interruptflag)
  { 
    
    displaySignal();
    swapBuffers();

    interruptflag = false;
  }

  if (receiveUDP() || readytosend)  // Start sending data when a connection is established
  {
    readytosend = true;

    uint16_t * tmpSendptr = sendptr;
    uint16_t * tmpSendptr2 = sendptr;

    if (sendEKGdata(false))  // Wenn erfolgreich Rohdaten gesendet wurden, die gefilterten auch senden
    {
      Serial.print("Das ist der Wert von tmpSendptr vor Filteraufruf");
      Serial.println(*tmpSendptr);
      IIR_Notch_Filter(tmpSendptr);                                 // gefilterten Sensorwert berechnen (Filter 2. Ordnung)
      sendptr = tmpSendptr2;
      sendEKGdata(true);
    }
    
  }
}